import React from "react";

class Stopwatch extends React.Component {
     constructor(props){
        super(props)
        this.state = {
            isOpen: false,
            time:0
        }
     }

     handleclick = () => {
        this.setState({
            isOpen:true,

        })
     }

     render(){
        return (
            <>
            <div>
                <button onClick={this.handleclick}>
                    Stopwatch
                </button>
                </div>
                <div>
            {
                this.state.isOpen && (
                 <div>
                    <h2>Stopwatch</h2>
                    <>{this.state.time}</>
                 </div>
                )

                }
            </div>

            </>
        )
     }
}

export default Stopwatch
