import React from "react";

class Stopwatch extends React.Component {
     constructor(props){
        super(props)
        this.state = {
            isOpen: false,
            time : {
                ms:0,
                s:0,
                m:0,
                h:0
            },
            setTime: {
                ms:0,
                s:0,
                m:0,
                h:0
            }

        }
     }


     handleclick = () => {
        this.setState({
            isOpen:true,

        })
     }

     handleStart = () =>{
        const updatems = this.state.time.ms
        const updates = this.state.time.s
        const updatem = this.state.time.m
        const updateh = this.state.time.h

        if(updatem === 60){
            updateh++
            updatem = 0
        }
        if(updates === 60){
            updatem++
            updates = 0
        }
        if(updatems === 60){
            updates++
            updatems = 0
        }
        updatems++
         this.setState({
            SetTime :{ ms: updatems,
                h: updateh,
                s:updates,
                m:updatem 
            }
        })

     }



     render(){
        return (
            <>
            <div>
                <button onClick={this.handleclick}>
                    Stopwatch
                </button>
                </div>
                <div>
            {
                this.state.isOpen && (
                 <div>
                    <h2>Stopwatch</h2>
                    <span>{(this.state.time.h >= 10)? this.state.time.h:'0' + this.state.time.h}</span>   :  
                    <span>{(this.state.time.m >= 10)? this.state.time.m:'0' + this.state.time.m}</span>   :
                    <span>{(this.state.time.s >= 10)? this.state.time.s:'0' + this.state.time.s}</span>   :
                    <span>{(this.state.time.ms >= 10)? this.state.time.ms:'0' + this.state.time.ms}</span>    
                    <button onClick={this.handleStart}
                    >Start</button>          
                 </div>

                )   
                }
            </div>

            </>
        )
     }
}

export default Stopwatch
