import React from "react";

class Stopwatch extends React.Component {
     constructor(props){
        super(props)
        this.state = {
            isOpen: false,
            time : 0,
            timerOn:false,
            timerStart: 0,

        }
        var Interval;

     }


     handleclick = () => {
        this.setState({
            isOpen:true,

        })
     }

     startTimer = () => {
        this.setState({
          timerOn: true,
          timerTime: this.state.time,
          timerStart: Date.now() - this.state.time
        });
        this.timer = setInterval(() => {
          console.log(`Time is ðŸš€ ${Date.now() - this.state.timerStart}`);
          this.setState({
            timerTime: Date.now() - this.state.timerStart
          });
        }, 10);
      };




     render(){
        const { time } = this.state;
          let miliseconds = ("0" + Math.floor(time))
           let seconds = ("0" + Math.floor(time))
          let minutes = ("0" + Math.floor(time ))
          let hours = ("0" + Math.floor(time))
        return (
            <>
            <div>
                <button onClick={this.handleclick}>
                    Stopwatch
                </button>
                </div>
                <div>
            {
                this.state.isOpen && (
                 <div>
                    <h2>Stopwatch</h2>
                     <span>{hours}:{minutes}:{seconds}:{miliseconds}</span>
                     {this.state.timerOn === false && this.state.time === 0 && (
                    <button onClick={this.startTimer}>Start</button>
                   )}         
                 </div>

                )   
                }
            </div>

            </>
        )
     }
}

export default Stopwatch
